from django import templatefrom basic.metatags.models import Metatagregister = template.Library()@register.inclusion_tag('metatags.html')def get_metatags(obj):    """    Gets a list of html metatags for the given object. Returns the list     as the given varible name.        Syntax::        {% get_metatags [object] %}    Example usage::        {% get_metatags flatpage %}    """    metatags = Metatag.objects.for_model(obj)    return {'metatags': metatags}