from django import templatefrom luminous.pages.models import PageImageregister = template.Library()@register.simple_tagdef get_pageimagetag(obj):    """    Gets an image associated with the given flatpage.         Syntax::        {% get_pageimage [object] %}    Example usage::        {% get_pageimage flatpage %}    """    pageimage = PageImage.objects.get(flatpage=obj)    if pageimage:        return '<img src="%s" alt="%s" />' % (pageimage.get_pageimag)